mapper.select:
SELECT *
FROM wish
WHERE member_id = #{memberId}
AND product_id = #{productId}

SELECT COUNT(*) > 0
FROM wish
WHERE member_id = #{memberId}
AND product_id = #{productId}
AND is_deleted = false

SELECT product_id
FROM wish
WHERE member_id = #{memberId}
AND is_deleted = false
ORDER BY created_at DESC 
LIMIT #{size}
OFFSET #{offset}
